export default function FirstPage (props) {
  const [hints, setHints] = useState([])

  useEffect(() => {
    
  })

  function fetchApiHints(){

    fetch("https://api.bing.microsoft.com/"
    + "v7.0/Suggestions?"
    + "&mkt=en-US"
    + "&setLang=en-US"
    + "&q=" + props.mainInput, {
      headers: {
        'Ocp-Apim-Subscription-Key' : subscriptionKey,
      }
    })
    .then(response => response.json())
    .then((data, fetchedDesriptions) => fetchedDesriptions = data.suggestionGroups[0].searchSuggestions.map(elem => elem.displayText))
    .then((data) => setHints(data))
    .catch(err => console.log(err))
  }
  async function handleChange(event){
    await props.setMainInput(event.target.value)

    if(event.target.value.length > 2){
      fetchApiHints()
      console.log("fetched")
    }

  }
  function handleSubmit(event){
    event.preventDefault()
    props.changePage()
  }
  function clearInput(){ 
    const inp = document.querySelector(".contentDiv__form__input")
    if(inp.value === ""){
      return;
    }
    props.setMainInput("")
    inp.value = ""
    console.log(hints)
  }
  return(
      <div className="firstPageMainDiv" id="firstPageMainDiv">
        <img className="backGroundImg" alt="landscape" src={backgroundImage} />
        <div className="contentDiv" id="contentDiv">
          <h1 className="contentDiv__h1">Look For An Image</h1>
          <p className="contentDiv__firstSubtitle">Usplash API powered pictures browser.</p>
          <form onSubmit={handleSubmit} className="contentDiv__form">
            <button onClick={handleSubmit} title="Search for pictures" type="button" id="contentDiv__form__lupeBtn"  className="contentDiv__form__lupeBtn" ><img className="lupeIcon" alt="lupe_icon" src={lupe} /></button>
            <input list="autocomplite" autoComplete="off" required onChange={handleChange} id="contentDiv__form__input" className="contentDiv__form__input" type="search" placeholder="Search for high-resolution photos" />
            <datalist id="autocomplite">
              {hints.length > 0 ? hints.map(elem => {
                return <option key={elem} value={elem}>{elem}</option>
              }) : props.mainInput.length > 2 ? <option>No hints for chosen query</option> : null}
            </datalist>
            <button title="Clear form" type="button" onClick={clearInput} id="contentDiv__form__clearBtn" className="contentDiv__form__clearBtn"><img className="crossIcon" alt="cross_icon" src={cross} /></button>
          </form>
          <p className="contentDiv__trending">There are available only 50 API requests per hour so this can expire quickly.</p>
        </div>
      </div>
    )
}
