import React, { useEffect, useState } from 'react';
import './App.css';
import lupe from './icons/loupe.png'
import cross from "./icons/letter-x.png"
import locationIcon from './icons/pin.png'

const APIAccessKey = "6PMB_sssC924TiZ3jPaY4Iwo4KZ0E6d6xZ0dgSbK4_g";
// src="https://images.unsplash.com/photo-1494500764479-0c8f2919a3d8?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80"

function FirstPage(props) {
  // const [input, setInput] = useState("")
  function handleChange(event){
    props.setMainInput(event.target.value)
  }
  function handleSubmit(event){
    event.preventDefault()
    props.changePage()
  }
  function clearInput(){ 
    const inp = document.querySelector(".contentDiv__form__input")
    if(inp.value === ""){
      return;
    }
    props.setMainInput("")
    inp.value = ""
  }
  return(
    <div className="firstPageMainDiv">
      <img className="backGroundImg" alt="landscape" src="https://images.unsplash.com/photo-1494500764479-0c8f2919a3d8?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80" />
      <div className="contentDiv" id="contentDiv">
        <h1 className="contentDiv__h1">Look For An Image</h1>
        <p className="contentDiv__firstSubtitle">Usplash API powered pictures browser.</p>
        <form onSubmit={handleSubmit} className="contentDiv__form">
          <button onClick={handleSubmit} title="Search for pictures" type="button" id="contentDiv__form__lupeBtn"  className="contentDiv__form__lupeBtn" ><img className="lupeIcon" alt="lupe_icon" src={lupe} /></button>
          <input required onChange={handleChange} id="contentDiv__form__input" className="contentDiv__form__input" type="search" placeholder="Search for high-resolution photos" />
          <button title="Clear form" type="button" onClick={clearInput} id="contentDiv__form__clearBtn" className="contentDiv__form__clearBtn"><img className="crossIcon" alt="cross_icon" src={cross} /></button>
        </form>
        <p className="contentDiv__trending">Trending: </p>
      </div>
    </div>
  )
}


function OpenedImage(props){
  function closeImage(){
    props.openImage();
  }
  function closeModal(event){
    // if(event.target.id === "outerPicHolder" || event.target.id === "openedImageMainDiv"){
    //   console.log(event.target)
    // }
  }
  return(
    <div onClick={closeModal} className="openedImageMainDiv" id="openedImageMainDiv">
      <img className="modalCancelBtn" alt="cross_icon" src={cross} onClick={closeImage} />
      <div className="outerPicHolder" id="outerPicHolder">
        <div className="innerPicHolder" id="innerPicHolder">
          <div className="mainInfoDiv">
              <div className="innerInfoDiv">
                <img className="profile_photo" alt="profile_photo" src={props.profileImg} />
                <div className="userInfoDiv">
                  <p className="nameP">{props.name}</p>
                  <p className="usernameP">@{props.username}</p>
                </div>
              </div>
              <p className="locationP">{props.location}</p>
              <img alt="location_icon" src={locationIcon} className="locationIcon" />       
          </div>
          <div className="outerPicDiv">
            <div className="picDiv">
              <img className="pic" alt="pic" src={props.rawImg} />
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}


class SecondPage extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      showParticularPic: false,
      images: [],
      leftImages: [],
      midImages: [],
      rightImages: [],
      query: props.mainInput.charAt(0).toUpperCase() + props.mainInput.slice(1)
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.clearInput = this.clearInput.bind(this);
    this.openImage = this.openImage.bind(this);
    this.setPicData = this.setPicData.bind(this);
  }
  componentDidMount(){
    this.setState({
      images: [],
      leftImages: [],
      midImages: [],
      rightImages: [],
      imgId: "",
      smallImg: "",
      rawImg: "",
      name: "",
      username: "",
      location: "",
      profileImg: ""
    })
    fetch("https://api.unsplash.com/search/photos/?"
    + "&client_id=" + APIAccessKey
    + "&query=" + this.props.mainInput
    + "&page=1"
    )
    .then(response => response.json())
    .then((data, fetchedUrls) => fetchedUrls = data.results.map(elem => [elem.urls.small, elem.urls.raw, elem.user.name,
    elem.user.username, elem.user.location, elem.user.profile_image.medium, elem.id]))
    .then(data => this.setState({images: data}))
    .then(data => this.setState({leftImages: this.state.images.filter((function(_, i){return i % 3 === 0}))}))
    .then(data => this.setState({midImages: this.state.images.slice(1).filter((function(_, i){return i % 3 === 0}))}))
    .then(data => this.setState({rightImages: this.state.images.slice(2).filter((function(_, i){return i % 3 === 0}))}))
    .catch(err => this.setState({images: []}))
  }
  handleChange(event){
    this.props.setMainInput(event.target.value)
  }
  handleSubmit(event){
    event.preventDefault();

    this.componentDidMount()

    const upperCaseInputFirstChar = this.props.mainInput.charAt(0).toUpperCase() + this.props.mainInput.slice(1);
    this.setState({
      query: upperCaseInputFirstChar
    })
  }
  clearInput(){ 
    const inp = document.querySelector(".secondPage__form__search")
    if(inp.value === ""){
      return;
    }
    this.props.setMainInput("")
    inp.value = ""
  }
  openImage(event){
    this.setState({
      showParticularPic: !this.state.showParticularPic
    })
  }
  setPicData(event){

    this.openImage()

    if(this.state.showParticularPic === true){
      return;
    } else {
      const id = event.target.id
      const pic = []
      this.state.images.map(elem => elem.includes(id) ? pic.push(elem) : null)
      this.setState({
        imgId: pic[0][6],
        rawImg: pic[0][1],
        name: pic[0][2],
        username: pic[0][3],
        location: pic[0][4],
        profileImg: pic[0][5]
    })
    }
  }
  render(){
  return(
    <div className="secondPageMainDiv" id="secondPageMainDiv">
      <form className="secondPage__form" onSubmit={this.handleSubmit}>
        <button onClick={this.handleSubmit} title="Search for pictures" type="button" className="secondPage__form__lupeBtn" ><img className="lupeIcon" alt="lupe_icon" src={lupe} /></button>
        <input title="Search for pictures" required onChange={this.handleChange} placeholder="Search..." className="secondPage__form__search" type="search" />
        <button title="Clear form" type="button" onClick={this.clearInput} className="secondPage__form__clearBtn"><img className="crossIcon" alt="cross_icon" src={cross} /></button>
      </form>
      <h1 className="secondPage__h1">{this.state.query}</h1>
      <div className="secondPage__blocks">Klocki z podobnymi wyszukiwaniami</div>
      <div className="secondPage__imagesContainer">
        <div className="leftImagesDiv">
          {this.state.leftImages.length > 0 ? this.state.leftImages.map(elem => {
            return (
              <div id="imgHolder" onClick={this.setPicData} key={elem[6]} imgid={elem[6]} className="leftImageHolder"><img id={elem[6]} className="leftImg" alt="opis" src={elem[0]} /></div>
            )
          }) : <h2 style={{color: "red"}}>Zapytanie nie zwróciło wyników</h2> }
        </div>
        <div className="midImagesDiv">
          {this.state.midImages.map(elem => {
              return (
                <div  onClick={this.setPicData} key={elem[6]} imgid={elem[6]} className="midImageHolder"><img id={elem[6]} className="midImg" alt="opis" src={elem[0]} /></div>
              )
            })}
        </div>
        <div className="rightImagesDiv">
          {this.state.rightImages.map(elem => {
              return (
                <div id="imgHolder" onClick={this.setPicData} key={elem[6]} imgid={elem[6]} className="rightImageHolder"><img id={elem[6]} className="rightImg" alt="opis" src={elem[0]} /></div>
              )
            })}
        </div>
      </div>
      {this.state.showParticularPic ? <OpenedImage id={this.state.imgId} rawImg={this.state.rawImg} openImage={this.openImage}
      name={this.state.name} username={this.state.username} location={this.state.location} profileImg={this.state.profileImg} /> : null}
    </div>
  )}
}

function App() {
  const [api, setAPI] = useState("");
  const [showFirstPage, setShowFirstPage] = useState(true)
  const [showSecondPage, setShowSecondPage] = useState(false)
  const [mainInput, setMainInput] = useState("");

  function changePages(){
    setShowFirstPage(!showFirstPage);
    setShowSecondPage(!showSecondPage);
  }
  return (
    <div className="App">
      {showFirstPage ? <FirstPage changePage={changePages} url={api} setMainInput={setMainInput} /> : null}
      {showSecondPage ? <SecondPage setMainInput={setMainInput} mainInput={mainInput} /> : null}
    </div>
  );
}

export default App;
