import { useEffect, useState } from 'react';
import './App.css';
import lupe from './icons/loupe.png'
import cross from "./icons/letter-x.png"

const APIAccessKey = "6PMB_sssC924TiZ3jPaY4Iwo4KZ0E6d6xZ0dgSbK4_g";
const APISecretKey = "eYD6_TkHUu5qhbAmLSx28hFlhl4101jKBTH_vNyJiVE";
// src="https://images.unsplash.com/photo-1494500764479-0c8f2919a3d8?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80"

function FirstPage(props) {
  // const [input, setInput] = useState("")
  function handleChange(event){
    props.setMainInput(event.target.value)
  }
  function handleSubmit(event){
    event.preventDefault()
    props.changePage()
  }
  function clearInput(){ 
    const inp = document.querySelector(".contentDiv__form__input")
    if(inp.value === ""){
      return;
    }
    props.setMainInput("")
    inp.value = ""
  }
  return(
    <div className="firstPageMainDiv">
      <img className="backGroundImg" alt="landscape" src="https://images.unsplash.com/photo-1494500764479-0c8f2919a3d8?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80" />
      <div className="contentDiv">
        <h1 className="contentDiv__h1">Look For An Image</h1>
        <p className="contentDiv__firstSubtitle">Usplash API powered pictures browser.</p>
        <form onSubmit={handleSubmit} className="contentDiv__form">
          <button onClick={handleSubmit} title="Search for pictures" type="button" className="contentDiv__form__lupeBtn" ><img className="lupeIcon" alt="lupe_icon" src={lupe} /></button>
          <input required onChange={handleChange} className="contentDiv__form__input" type="search" placeholder="Search for high-resolution photos" />
          <button title="Clear form" type="button" onClick={clearInput} className="contentDiv__form__clearBtn"><img className="crossIcon" alt="cross_icon" src={cross} /></button>
        </form>
        <p className="contentDiv__trending">Trending: </p>
      </div>
    </div>
  )
}


function SecondPage(props){
  const [showParticularPic, setShowParticularPic] = useState(false);
  const [images, setImages] = useState([]);
  const [leftImages, setLeftImages] = useState([]);
  const [midImages, setMidtImages] = useState([]);
  const [rightImages, setRightImages] = useState([]);

  // useEffect(() => {
  //   const h1 = document.querySelector(".secondPage__h1");
  //   const upperCaseInputFirstChar = props.mainInput.charAt(0).toUpperCase() + props.mainInput.slice(1);
  //   h1.innerHTML = upperCaseInputFirstChar;
  // }, [props.mainInput])

  // useEffect(() => {
  //   const fetchedUrls = []
  //   fetch("https://api.unsplash.com/photos/random?"
  //   + "&client_id=" + APIAccessKey
  //   + "&query=" + props.mainInput
  //   + "&count=30"
  //   )
  //   .then(response => response.json())
  //   .then(data => console.log(data))
  //   // .then(data => data.map(elem => fetchedUrls.push(elem.urls.raw)))
  //   // .then(setImages(fetchedUrls))
  // }, [props.mainInput])

  function handleChange(event){
    props.setMainInput(event.target.value)
  }
  function handleSubmit(event){
    event.preventDefault()
    const h1 = document.querySelector(".secondPage__h1");
    const upperCaseInputFirstChar = props.mainInput.charAt(0).toUpperCase() + props.mainInput.slice(1);
    h1.innerHTML = upperCaseInputFirstChar;
    console.log(props.mainInput)
    console.log(images)


    const fetchedUrls = []
    con
    fetch("https://api.unsplash.com/photos/random?"
    + "&client_id=" + APIAccessKey
    + "&query=" + props.mainInput
    + "&count=30"
    )
    .then(response => response.json())
    // .then(data => console.log(data))
    .then(data => data.map(elem => fetchedUrls.push(elem.urls.raw)))
    // .then(setImages(fetchedUrls))
    .then()
  }
  function clearInput(){ 
    const inp = document.querySelector(".secondPage__form__search")
    if(inp.value === ""){
      return;
    }
    props.setMainInput("")
    inp.value = ""
  }
  return(
    <div className="secondPageMainDiv">
      <form className="secondPage__form" onSubmit={handleSubmit}>
        <button onClick={handleSubmit} title="Search for pictures" type="button" className="secondPage__form__lupeBtn" ><img className="lupeIcon" alt="lupe_icon" src={lupe} /></button>
        <input title="Search for pictures" required onChange={handleChange} placeholder="Search..." className="secondPage__form__search" type="search" />
        <button title="Clear form" type="button" onClick={clearInput} className="secondPage__form__clearBtn"><img className="crossIcon" alt="cross_icon" src={cross} /></button>
      </form>
      <h1 className="secondPage__h1">_</h1>
      <div className="secondPage__blocks">Klocki z podobnymi wyszukiwaniami</div>
      <div className="secondPage__imagesContainer">
        <div className="leftImagesDiv">
          {images.map(elem => {
            return (
              <div className="leftImageHolder"><img className="leftImg" alt="opis" src={elem} /></div>
              // <img alt="opis" src={elem} />
            )
          })}
        </div>
        <div className="midImagesDiv">
          {images.map(elem => {
              return (
                <div className="midImageHolder"><img className="midImg" alt="opis" src={elem} /></div>
                // <img alt="opis" src={elem} />
              )
            })}
        </div>
        <div className="rightImagesDiv">
          {images.map(elem => {
              return (
                <div className="rightImageHolder"><img className="rightImg" alt="opis" src={elem} /></div>
                // <img alt="opis" src={elem} />
              )
            })}
        </div>
      </div>
    </div>

  )
}

function App() {
  const [api, setAPI] = useState("");
  const [landscapeUrl, setLandscapeURl] = useState("")
  const [showFirstPage, setShowFirstPage] = useState(true)
  const [showSecondPage, setShowSecondPage] = useState(false)
  const [mainInput, setMainInput] = useState("");
  // useEffect(() => {
  //   fetch("https://api.unsplash.com/photos/random?"
  //   + "&client_id=" + APIAccessKey
  //   + "&query=landscape"
  //   + "&orientation=landscape"
  //   + "&count=1"
  //   )
  //   .then(respond => respond.json())
  //   .then(data => setAPI(data.urls.raw))
  // }, [])
  function changePages(){
    setShowFirstPage(!showFirstPage);
    setShowSecondPage(!showSecondPage);
  }
  return (
    <div className="App">
      {showFirstPage ? <FirstPage changePage={changePages} url={api} setMainInput={setMainInput} /> : null}
      {showSecondPage ? <SecondPage setMainInput={setMainInput} mainInput={mainInput} /> : null}
    </div>
  );
}

export default App;
