export default function SecondPage() {

  const [showParticularPic, setShowParticularPic] = useState(false)
  const [images, setImages] = useState([])
  const [leftImages, setLeftImages] = useState([])
  const [midImages, setMidImages] = useState([])
  const [rightImages, setRightImages] = useState([])
  const [query, setQuery]= useState(props.mainInput.charAt(0).toUpperCase() + props.mainInput.slice(1))
  const [picOrientation, setPicOrientation] = useState("")
  const [imgId, setImgId] = useState("")
  const [smallImg, setSmaillImg] = useState("")
  const [rawImg, setRawImg] = useState("")
  const [name, setName] = useState("")
  const [username, setUsername] = useState("")
  const [location, setLocation] = useState("")
  const [profileImg, setProfileImg] = useState("")
  const [scrollNum, setScrollNum] = useState(1)
  const [imgsFromScroll, setImgsFromScroll]  = useState([])
  const [resizeImages, setResizeImages]  = useState([])
  const [tags, setTags]  = useState([])

  function fetchAPI(page){

    fetch("https://api.unsplash.com/search/photos/?"
    + "&client_id=" + APIAccessKey
    + "&query=" + props.mainInput
    + "&per_page=30"
    + "&page=" + page
    )
    .then(response => response.json())
    .then((data, fetchedUrls) => fetchedUrls = data.results.map(elem => [elem.urls.small, elem.urls.regular, elem.user.name,
    elem.user.username, elem.user.location, elem.user.profile_image.medium, elem.id, elem.alt_description, elem.tags]))
    .then(data => setImages(data.length > 0 ? data : "empty"))
    .then(data => setLeftImages(images === "empty" ? leftImages : leftImages.concat(images.filter((function(_, i){return i % 3 === 0})))))
    .then(data => setMidImages(images === "empty" ? midImages : midImages.concat(images.slice(2).filter((function(_, i){return i % 3 === 0})))))
    .then(data => setRightImages(images === "empty" ?  rightImages : rightImages.concat(images.slice(1).filter((function(_, i){return i % 3 === 0})))))
    .then(data => setResizeImages(images === "empty" ?  resizeImages : resizeImages.concat(images)))
    .then(gettags => gettags =  images.map(elem => elem[8].length > 1 ? elem[8][1] : null).filter(elem => elem !== null)
    .map(elem => elem.title).filter(e => e !== props.mainInput).filter(e => e.length <= 16))
    .then(data => setTags([...new Set(data)]))
    .catch(err => console.log(err))

    // to hide no hints alert and tags
    clearInput()
    setTags([])
  }
  function scrollUpdate(){
    if(showParticularPic === true || images === "empty"){
      return;
    }
    const lastDiv = document.querySelector(".secondPage__imagesContainer")
    if(lastDiv.getBoundingClientRect().bottom -1 < window.innerHeight){
      fetchAPI(scrollNum + 1);
      setScrollNum(scrollNum + 1)
    }
  }
  function showOnImageInfo(event){

    setPicData(event)
    
    const id = event.target.id
    const userInfoDiv = document.getElementById("onImg__innerInfoDiv"+id)
    const resizeUserInfoDiv = document.getElementById("resize__onImg__innerInfoDiv"+id)
    
    userInfoDiv.style["display"] = "flex";
    resizeUserInfoDiv.style["display"] = "flex";
  }
  function hideOnPicImageInfo(event){
    
    const id = event.target.id
    const userInfoDiv = document.getElementById("onImg__innerInfoDiv"+id)
    const resizeUserInfoDiv = document.getElementById("resize__onImg__innerInfoDiv"+id)
    userInfoDiv.style["display"] = "none";
    resizeUserInfoDiv.style["display"] = "none";
  }
  function clearLists(){
    setImages([])
    setLeftImages([])
    setMidImages([])
    setRightImages([])
    setResizeImages([])
  }
  useEffect(() => {

    fetchAPI(1)

    // scroll function
    window.addEventListener('scroll', scrollUpdate)

    // fetch on hints list click
    const inp = document.getElementById("secondPage__form__search")
    let eventSource = null;
    let value = '';

    inp.addEventListener('keydown', (e) => {
      eventSource = e.key ? 'input' : 'list';
    });
    inp.addEventListener('input', (e) => {
      value = e.target.value;
      if (eventSource === 'list') {
        clearLists();
        props.setMainInput(value);
        setQuery(value);
        fetchAPI(1);
      }
    });

  }, [props])
  async function handleChange(event){
    await props.setMainInput(event.target.value.toLowerCase())

    if(event.target.value.length > 2){
      props.fetchAPIHints()        
    } else {
      props.setHints([])
    }
  }
  function handleSubmit(event){
    event.preventDefault();

    clearLists();
    fetchAPI(1)

    setQuery(props.mainInput.charAt(0).toUpperCase() + props.mainInput.slice(1))
  }
  function clearInput(){ 
    const inp = document.querySelector(".secondPage__form__search")
    if(inp.value === ""){
      return;
    }
    inp.value = ""
    props.setHints([])
  }
  function openImage(event){
    setShowParticularPic(!showParticularPic)
  }
  function setPicData(event){
    const pic = []
    const id = event.target.id
    const picContainer = event.target.className
    if(picContainer === "leftImg"){
      leftImages.map(elem => elem.includes(id) ? pic.push(elem) : null)
    } 
    else if (picContainer === "midImg"){
      midImages.map(elem => elem.includes(id) ? pic.push(elem) : null)
    }
    else if (picContainer === "rightImg"){
      rightImages.map(elem => elem.includes(id) ? pic.push(elem) : null)
    }
    else if (picContainer === "resizeImg"){
      resizeImages.map(elem => elem.includes(id) ? pic.push(elem) : null)
    }
    
    clearLists();
  }
  function preparePicToOpen(event){

    setPicData(event)

    const picWidth = event.target.width
    const picHeight = event.target.height

    if(picWidth >= picHeight){
      setPicOrientation("horizontal") 
    }else{
      setPicOrientation("vertical") 
    }

    openImage()
  }
  function handleArrow(){
    props.changePage()
  }
  async function handleTag(event){
    
    const tag = event.target.id
    await props.setMainInput(tag)
    setQuery(tag.charAt(0).toUpperCase() + tag.slice(1))
    clearLists();
    fetchAPI(1);
  }
  function scrollRigth(){
    const innerTagsDiv = document.getElementById("innerTagsDiv")
    innerTagsDiv.scrollBy(350, 0);

    const lA = document.getElementById("leftArrowHolder")
    lA.style["visibility"] = "visible";

    const tagHolderslist = document.querySelectorAll(".tagHolder")
    const rA = document.getElementById("rightArrowHolder")
    if(tagHolderslist[tagHolderslist.length -1].getBoundingClientRect().right < rA.getBoundingClientRect().right){
      rA.style["visibility"] = "hidden";
    }
  }
  function scrollLeft(){
    const innerTagsDiv = document.getElementById("innerTagsDiv")
    innerTagsDiv.scrollBy(-350, 0);

    const rA = document.getElementById("rightArrowHolder")
    rA.style["visibility"] = "visible";

    const tagHolderslist = document.querySelectorAll(".tagHolder")
    const lA = document.getElementById("leftArrowHolder")
    if(tagHolderslist[0].getBoundingClientRect().left > lA.getBoundingClientRect().left){
      lA.style["visibility"] = "hidden";
      }
  }
  useEffect(() => {
    if(tags.length > 1){
      
      const rA = document.getElementById("rightArrowHolder")
      const tagHolderslist = document.querySelectorAll(".tagHolder")
      if(tagHolderslist[tagHolderslist.length -1].getBoundingClientRect().right > rA.getBoundingClientRect().left &&
      tagHolderslist[tagHolderslist.length -1].getBoundingClientRect().right > rA.getBoundingClientRect().right){
        rA.style["visibility"] = "visible";
      }
    }
  }, [])
  return(
    <div className="secondPageMainDiv" id="secondPageMainDiv">
      <img onClick={handleArrow} alt="left_arrow_icon" src={arrow} title="Go back" className="arrowBack" id="arrowBack" />
      <form className="secondPage__form" onSubmit={handleSubmit}>
        <button onClick={handleSubmit} title="Search for pictures" type="button" className="secondPage__form__lupeBtn" ><img className="lupeIcon" alt="lupe_icon" src={lupe} /></button>
        <input list="autocomplite" autoComplete="off" title="Search for pictures" required onChange={handleChange} placeholder="Search..." id="secondPage__form__search" className="secondPage__form__search" type="search" />
        <datalist id="autocomplite">
            {props.hints.map(elem => {
              return <option key={elem} value={elem}>{elem}</option>
            })}
          </datalist>
        <button title="Clear form" type="button" onClick={clearInput} className="secondPage__form__clearBtn"><img className="crossIcon" alt="cross_icon" src={cross} /></button>
      </form>
      <div className="alertDiv">
        <p className="alertP">{props.hints[0] === "No hints for choosen query" ? " -- " + props.hints[0] : null}</p>
      </div>
      <h1 className="secondPage__h1">{query}</h1>

      {tags.length > 1 ?
      <div className="mainTagsDiv" id="mainTagsDiv">
        <div className="leftArrowHolder" id="leftArrowHolder">
          <div onClick={scrollLeft} className="leftArrow"></div>
        </div>
        <div className="rightArrowHolder" id="rightArrowHolder">
          <div onClick={scrollRigth} className="rightArrow"></div>
        </div>
        <div className="innerTagsDiv" id="innerTagsDiv">
        {tags.map(elem => {
                return <div onClick={handleTag} id={elem} key={elem + "LI"} className="tagHolder">
                    <p id={elem} key={elem} className="tagP">{elem.charAt(0).toUpperCase() + elem.slice(1)}</p>
                      </div>
                    })}
        </div>
      </div> : null}
     
      <div className="secondPage__imagesContainer" id="secondPage__imagesContainer">

        {/* just for resize */}
        <div className="resizeImagesDiv" id="resizeImagesDiv">
          {resizeImages.length > 0 ? resizeImages.map(elem => {
              return (
                <div className="resizeImageHolder" id={"resizeImageHolder"+elem[6]} onClick={preparePicToOpen} key={elem[6]} imgid={elem[6]}>
                <img onMouseEnter={showOnImageInfo} onMouseLeave={hideOnPicImageInfo} id={elem[6]} className="resizeImg" alt={elem[7]} src={elem[0]} />
                  <div className="resize__onImg__innerInfoDiv" id={"resize__onImg__innerInfoDiv"+elem[6]}>
                    <img className="resize__onImg__profile_photo" alt="profile_photo" src={profileImg} />
                    <div className="resize__onImg__userInfoDiv">
                      <p className="resize__onImg__nameP">{name}</p>
                    </div>
                  </div>
                </div>
              )
            }) : <h2 style={{color: "red"}}>No results for given queries. Try with another ones.</h2> }
        </div>
        <div className="leftImagesDiv" id="leftImagesDiv">

          {leftImages.length > 0 ? leftImages.map(elem => {
            return (
              <div className="leftImageHolder" id={"leftImageHolder"+elem[6]} onClick={preparePicToOpen} key={elem[6]} imgid={elem[6]}>
                <img onMouseEnter={showOnImageInfo} onMouseLeave={hideOnPicImageInfo} id={elem[6]} className="leftImg" alt={elem[7]} src={elem[0]} title={elem[7]} />
                <div className="onImg__innerInfoDiv" id={"onImg__innerInfoDiv"+elem[6]}>
                  <img className="onImg__profile_photo" alt="profile_photo" src={profileImg} />
                  <div className="onImg__userInfoDiv">
                    <p className="onImg__nameP">{name}</p>
                  </div>
                </div>
              </div>
            )
          }) : <h2 style={{color: "red"}}>No results for given queries. Try with another ones.</h2> }
        </div>
        <div className="midImagesDiv" id="midImagesDiv">
          {midImages.map(elem => {
              return (
                <div className="midImageHolder" id={"midImageHolder"+elem[6]} onClick={preparePicToOpen} key={elem[6]} imgid={elem[6]}>
                  <img onMouseEnter={showOnImageInfo} onMouseLeave={hideOnPicImageInfo} id={elem[6]} className="midImg" alt={elem[7]} src={elem[0]} title={elem[7]} />
                  <div className="onImg__innerInfoDiv" id={"onImg__innerInfoDiv"+elem[6]}>
                    <img className="onImg__profile_photo" alt="profile_photo" src={profileImg} />
                    <div className="onImg__userInfoDiv">
                      <p className="onImg__nameP">{name}</p>
                    </div>
                  </div>
                </div>
              )
            })}
        </div>
        <div className="rightImagesDiv" id="rightImagesDiv">
          {rightImages.map(elem => {
              return (
              <div className="rightImageHolder" id={"rightImageHolder"+elem[6]} onClick={preparePicToOpen} key={elem[6]} imgid={elem[6]}>
                <img onMouseEnter={showOnImageInfo} onMouseLeave={hideOnPicImageInfo} id={elem[6]} className="rightImg" alt={elem[7]} title={elem[7]} src={elem[0]} />
                <div className="onImg__innerInfoDiv" id={"onImg__innerInfoDiv"+elem[6]}>
                  <img className="onImg__profile_photo" alt="profile_photo" src={profileImg} />
                  <div className="onImg__userInfoDiv">
                    <p className="onImg__nameP">{name}</p>
                  </div>
                </div>
              </div>
              )
            })}
        </div>
      </div>
      {images === "empty" && leftImages.length > 1 ? <p id="noMoreImgs">No more photos to display for '{query}' query.</p> : null}
      {showParticularPic ? <OpenedImage id={imgId} rawImg={rawImg} openImage={openImage}
      name={name} username={username} location={location} profileImg={profileImg} 
      picOrientation={picOrientation}/> : null}
    </div>
  )
}
