import React, { useEffect, useState } from 'react';
import './App.css';
import lupe from './icons/loupe.png'
import cross from "./icons/letter-x.png"
import locationIcon from './icons/pin.png'
import backgroundImage from "./icons/john-towner-JgOeRuGD_Y4-unsplash.jpg"
import OpenImage from '.openImage.js'

const APIAccessKey = "6PMB_sssC924TiZ3jPaY4Iwo4KZ0E6d6xZ0dgSbK4_g";

function FirstPage(props) {
  useEffect(() => {
    console.log(window.screen.width)
  })
  function handleChange(event){
    props.setMainInput(event.target.value)
  }
  function handleSubmit(event){
    event.preventDefault()
    props.changePage()
  }
  function clearInput(){ 
    const inp = document.querySelector(".contentDiv__form__input")
    if(inp.value === ""){
      return;
    }
    props.setMainInput("")
    inp.value = ""
  }
  return(
    <div className="firstPageMainDiv" id="firstPageMainDiv">
      <img className="backGroundImg" alt="landscape" src={backgroundImage} />
      <div className="contentDiv" id="contentDiv">
        <h1 className="contentDiv__h1">Look For An Image</h1>
        <p className="contentDiv__firstSubtitle">Usplash API powered pictures browser.</p>
        <form onSubmit={handleSubmit} className="contentDiv__form">
          <button onClick={handleSubmit} title="Search for pictures" type="button" id="contentDiv__form__lupeBtn"  className="contentDiv__form__lupeBtn" ><img className="lupeIcon" alt="lupe_icon" src={lupe} /></button>
          <input required onChange={handleChange} id="contentDiv__form__input" className="contentDiv__form__input" type="search" placeholder="Search for high-resolution photos" />
          <button title="Clear form" type="button" onClick={clearInput} id="contentDiv__form__clearBtn" className="contentDiv__form__clearBtn"><img className="crossIcon" alt="cross_icon" src={cross} /></button>
        </form>
        <p className="contentDiv__trending">There are available only 50 API requests per hour so this can expire quickly.</p>
      </div>
    </div>
  )
}





class SecondPage extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      showParticularPic: false,
      images: [],
      leftImages: [],
      midImages: [],
      rightImages: [],
      query: props.mainInput.charAt(0).toUpperCase() + props.mainInput.slice(1),
      picOrientation: "",
      imgId: "",
      smallImg: "",
      rawImg: "",
      name: "",
      username: "",
      location: "",
      profileImg: "",
      scrollnum: 1,
      imgsFromScroll: [],
      resizeImages: []
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.clearInput = this.clearInput.bind(this);
    this.openImage = this.openImage.bind(this);
    this.setPicData = this.setPicData.bind(this);
    this.fetchAPI = this.fetchAPI.bind(this);
    this.scrollUpdate = this.scrollUpdate.bind(this);
    this.preparePicToOpen = this.preparePicToOpen.bind(this);
  }
  fetchAPI(page){
    fetch("https://api.unsplash.com/search/photos/?"
    + "&client_id=" + APIAccessKey
    + "&query=" + this.props.mainInput
    + "&page=" + page
    + "per_page=9"
    )
    .then(response => response.json())
    .then((data, fetchedUrls) => fetchedUrls = data.results.map(elem => [elem.urls.small, elem.urls.regular, elem.user.name,
    elem.user.username, elem.user.location, elem.user.profile_image.medium, elem.id]))
    .then(data => this.setState({images: data}))
    .then(data => this.setState({leftImages: this.state.leftImages.concat(this.state.images.filter((function(_, i){return i % 3 === 0})))}))
    .then(data => this.setState({midImages: this.state.midImages.concat(this.state.images.slice(1).filter((function(_, i){return i % 3 === 0})))}))
    .then(data => this.setState({rightImages: this.state.rightImages.concat(this.state.images.slice(2).filter((function(_, i){return i % 3 === 0})))}))
    .then(data => this.setState({resizeImages: this.state.resizeImages.concat(this.state.images.filter((function(_, i){return i % 3 === 0})))}))
    .catch(err => this.setState({images: []}))
  }
  scrollUpdate(){
    if(this.state.showParticularPic === true){
      return;
    }
    const lastDiv = document.querySelector(".secondPage__imagesContainer")
    if(lastDiv.getBoundingClientRect().bottom -1 < window.innerHeight){
      this.fetchAPI(this.state.scrollnum + 1);
      this.setState({
        scrollnum: this.state.scrollnum + 1
      })
    }
  }
  componentDidMount(){

    this.fetchAPI(1)

    // scroll function
    window.addEventListener('scroll', this.scrollUpdate)

  }
  handleChange(event){
    this.props.setMainInput(event.target.value)
  }
  handleSubmit(event){
    event.preventDefault();

    this.setState({
      images: [],
      leftImages: [],
      midImages: [],
      rightImages: [],
      resizeImages: []
    })
    this.fetchAPI(1)

    const upperCaseInputFirstChar = this.props.mainInput.charAt(0).toUpperCase() + this.props.mainInput.slice(1);
    this.setState({
      query: upperCaseInputFirstChar
    })
  }
  clearInput(){ 
    const inp = document.querySelector(".secondPage__form__search")
    if(inp.value === ""){
      return;
    }
    this.props.setMainInput("")
    inp.value = ""
  }
  openImage(event){
    this.setState({
      showParticularPic: !this.state.showParticularPic
    })
  }
  setPicData(event){
    const pic = []
    const id = event.target.id
    const picContainer = event.target.className
    if(picContainer === "leftImg"){
      this.state.leftImages.map(elem => elem.includes(id) ? pic.push(elem) : null)
    } 
    else if (picContainer === "midImg"){
      this.state.midImages.map(elem => elem.includes(id) ? pic.push(elem) : null)
    }
    else if (picContainer === "rightImg"){
      this.state.rightImages.map(elem => elem.includes(id) ? pic.push(elem) : null)
    }
    else if (picContainer === "resizeImg"){
      this.state.resizeImages.map(elem => elem.includes(id) ? pic.push(elem) : null)
    }

    this.setState({
      imgId: pic[0][6],
      rawImg: pic[0][1],
      name: pic[0][2],
      username: pic[0][3],
      location: pic[0][4],
      profileImg: pic[0][5]
    })
  }
  preparePicToOpen(event){

    this.setPicData(event)

    const picWidth = event.target.width
    const picHeight = event.target.height

    if(picWidth >= picHeight){
      this.setState({
        picOrientation: "horizontal"
      }) 
    }else{
      this.setState({
        picOrientation: "vertical"
      }) 
    }

    this.openImage()
  }
  render(){
  return(
    <div className="secondPageMainDiv" id="secondPageMainDiv">
      <form className="secondPage__form" onSubmit={this.handleSubmit}>
        <button onClick={this.handleSubmit} title="Search for pictures" type="button" className="secondPage__form__lupeBtn" ><img className="lupeIcon" alt="lupe_icon" src={lupe} /></button>
        <input title="Search for pictures" required onChange={this.handleChange} placeholder="Search..." className="secondPage__form__search" type="search" />
        <button title="Clear form" type="button" onClick={this.clearInput} className="secondPage__form__clearBtn"><img className="crossIcon" alt="cross_icon" src={cross} /></button>
      </form>
      <h1 className="secondPage__h1">{this.state.query}</h1>
      <div className="secondPage__imagesContainer">

        {/* just for resize */}
        <div className="resizeImagesDiv" id="resizeImagesDiv">
          {this.state.resizeImages.length > 0 ? this.state.resizeImages.map(elem => {
              return (
                <div id="imgHolder" onClick={this.preparePicToOpen} key={elem[6]} imgid={elem[6]} className="resizeImageHolder"><img id={elem[6]} className="resizeImg" alt="opis" src={elem[0]} /></div>
              )
            }) : <h2 style={{color: "red"}}>Zapytanie nie zwróciło żadnych wyników. Spróbuj innego hasła.</h2> }
        </div>
        <div className="leftImagesDiv" id="leftImagesDiv">

          {this.state.leftImages.length > 0 ? this.state.leftImages.map(elem => {
            return (
              <div id="imgHolder" onClick={this.preparePicToOpen} key={elem[6]} imgid={elem[6]} className="leftImageHolder"><img id={elem[6]} className="leftImg" alt="opis" src={elem[0]} />
              </div>
            )
          }) : <h2 style={{color: "red"}}>Zapytanie nie zwróciło wyników. Spróbuj innego hasła.</h2> }
        </div>
        <div className="midImagesDiv" id="midImagesDiv">
          {this.state.midImages.map(elem => {
              return (
                <div  onClick={this.preparePicToOpen} key={elem[6]} imgid={elem[6]} className="midImageHolder"><img id={elem[6]} className="midImg" alt="opis" src={elem[0]} /></div>
              )
            })}
        </div>
        <div className="rightImagesDiv" id="rightImagesDiv">
          {this.state.rightImages.map(elem => {
              return (
                <div id="imgHolder" onClick={this.preparePicToOpen} key={elem[6]} imgid={elem[6]} className="rightImageHolder"><img id={elem[6]} className="rightImg" alt="opis" src={elem[0]} /></div>
              )
            })}
        </div>
      </div>
      {this.state.showParticularPic ? <OpenedImage id={this.state.imgId} rawImg={this.state.rawImg} openImage={this.openImage}
      name={this.state.name} username={this.state.username} location={this.state.location} profileImg={this.state.profileImg} 
      picOrientation={this.state.picOrientation}/> : null}
    </div>
  )}
}

function App() {
  const [showFirstPage, setShowFirstPage] = useState(true)
  const [showSecondPage, setShowSecondPage] = useState(false)
  const [mainInput, setMainInput] = useState("");

  function changePages(){
    setShowFirstPage(!showFirstPage);
    setShowSecondPage(!showSecondPage);
  }

  return (
    <div className="App">
      {showFirstPage ? <FirstPage changePage={changePages} setMainInput={setMainInput} /> : null}
      {showSecondPage ? <SecondPage setMainInput={setMainInput} mainInput={mainInput} /> : null}
    </div>
  );
}

export default App;
