class SecondPage extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      showParticularPic: false,
      images: [],
      leftImages: [],
      midImages: [],
      rightImages: [],
      query: props.mainInput.charAt(0).toUpperCase() + props.mainInput.slice(1)
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.clearInput = this.clearInput.bind(this);
  }
  componentDidMount(){
    const fetchedUrls = [];

    fetch("https://api.unsplash.com/photos/random?"
    + "&client_id=" + APIAccessKey
    + "&query=" + this.props.mainInput
    + "&count=33"
    )
    .then(response => response.json())
    .then(data => data.map(elem => fetchedUrls.push(elem.urls.raw)))
    .then(this.setState({images: fetchedUrls}))
    .then(this.setState({leftImages: this.state.images.filter((function(_, i){return i % 3 === 0}))}))
    .then(this.setState({midImages: this.state.images.slice(1).filter((function(_, i){return i % 3 === 0}))}))
    .then(this.setState({midImages: this.state.images.slice(2).filter((function(_, i){return i % 3 === 0}))}))
    .catch(err => this.setState({images: []}))
  }
  handleChange(event){
    this.props.setMainInput(event.target.value)
  }
  handleSubmit(event){
    event.preventDefault()

    const fetchedUrls = [];

    fetch("https://api.unsplash.com/photos/random?"
    + "&client_id=" + APIAccessKey
    + "&query=" + this.props.mainInput
    + "&count=33"
    )
    .then(response => response.json())
    .then(data => data.map(elem => fetchedUrls.push(elem.urls.raw)))
    .then(this.setState({images: fetchedUrls}))
    .then(this.setState({leftImages: this.state.images.filter((function(_, i){return i % 3 === 0}))}))
    .then(this.setState({midImages: this.state.images.slice(1).filter((function(_, i){return i % 3 === 0}))}))
    .then(this.setState({midImages: this.state.images.slice(2).filter((function(_, i){return i % 3 === 0}))}))
    .catch(err => this.setState({images: []}))

    const upperCaseInputFirstChar = this.props.mainInput.charAt(0).toUpperCase() + this.props.mainInput.slice(1);
    this.setState({
      query: upperCaseInputFirstChar
    })
  }
  clearInput(){ 
    const inp = document.querySelector(".secondPage__form__search")
    if(inp.value === ""){
      return;
    }
    this.props.setMainInput("")
    inp.value = ""
  }
  render(){
  return(
    <div className="secondPageMainDiv">
      <form className="secondPage__form" onSubmit={this.handleSubmit}>
        <button onClick={this.handleSubmit} title="Search for pictures" type="button" className="secondPage__form__lupeBtn" ><img className="lupeIcon" alt="lupe_icon" src={lupe} /></button>
        <input title="Search for pictures" required onChange={this.handleChange} placeholder="Search..." className="secondPage__form__search" type="search" />
        <button title="Clear form" type="button" onClick={this.clearInput} className="secondPage__form__clearBtn"><img className="crossIcon" alt="cross_icon" src={cross} /></button>
      </form>
      <h1 className="secondPage__h1">{this.state.query}</h1>
      <div className="secondPage__blocks">Klocki z podobnymi wyszukiwaniami</div>
      <div className="secondPage__imagesContainer">
        <div className="leftImagesDiv">
          {this.state.leftImages.length > 0 ? this.state.leftImages.map(elem => {
            return (
              <div className="leftImageHolder"><img className="leftImg" alt="opis" src={elem} /></div>
              // <img alt="opis" src={elem} />
            )
          }) : <h2 style={{color: "red"}}>Zapytanie nie zwróciło wyników</h2> }
        </div>
        <div className="midImagesDiv">
          {this.state.midImages.map(elem => {
              return (
                <div className="midImageHolder"><img className="midImg" alt="opis" src={elem} /></div>
                // <img alt="opis" src={elem} />
              )
            })}
        </div>
        <div className="rightImagesDiv">
          {this.state.rightImages.map(elem => {
              return (
                <div className="rightImageHolder"><img className="rightImg" alt="opis" src={elem} /></div>
                // <img alt="opis" src={elem} />
              )
            })}
        </div>
      </div>
    </div>
  )}
}

